var Croppuccino=new Class({version:"0.4",Implements:[Options,Events],options:{preview:false,minSize:100},initialize:function(b){this.setOptions(b);this.container=document.id(this.options.container);this.preview=this.options.preview;this.options.minSize=Math.max(this.options.minSize.toInt(),1);this.setUpImage()},setUpContainers:function(){this.imageCropper=new Element("div",{"class":"cp_image_cropper"});this.imageArea=new Element("div",{"class":"cp_image_area"}).inject(this.imageCropper);this.cropArea=new Element("div",{"class":"cp_crop_area"}).inject(this.imageArea);this.sliderContainer=new Element("div",{"class":"cp_slider_container"}).inject(this.imageCropper);this.smallerIcon=new Element("div",{"class":"cp_smaller_icon"}).inject(this.sliderContainer);this.sliderLeft=new Element("div",{"class":"cp_slider_left"}).inject(this.sliderContainer);this.slider=new Element("div",{"class":"cp_slider"}).inject(this.sliderContainer);this.knob=new Element("div",{"class":"cp_knob"}).inject(this.slider);this.sliderRight=new Element("div",{"class":"cp_slider_right"}).inject(this.sliderContainer);this.largerIcon=new Element("div",{"class":"cp_larger_icon"}).inject(this.sliderContainer);this.imageCropper.inject(this.container)},setUpImage:function(){this.image=new Element("img",{src:this.options.img_src,"class":"cp_target_image",styles:{visibility:"hidden"},events:{load:this.setUpAll.bind(this),error:this.fireEvent.pass("error",this)}}).inject(this.container);this.fireEvent("startLoad")},setUpDrag:function(){var b=new Drag(this.imageArea,{snap:0,style:false,preventDefault:true});b.addEvents({start:this.dragStart.bind(this),drag:this.dragMoving.bind(this),})},setUpSlider:function(){this.innerRange=(this.minEdge-this.maxViewport);this.outerRange=(this.maxViewport-this.options.minSize);var b=this.innerRange+this.outerRange;this.mySlider=new Slider(this.slider,this.knob,{offset:6,range:b,steps:b,initialStep:this.innerRange});this.mySlider.addEvent("change",this.render.bind(this));this.smallerIcon.addEvent("click",this.mySlider.set.pass(0,this.mySlider));this.largerIcon.addEvent("click",this.mySlider.set.pass(b,this.mySlider))},setUpGlass:function(){this.glassDiv=new Array();for(var b=0;b<4;b++){this.glassDiv[b]=new Element("div",{"class":"cp_image_select_glass",}).inject(this.imageArea)}},setUpPreview:function(){this.previewImage=new Array();this.preview.each(function(c){var b=new Element("img",{src:this.options.img_src,"class":"cp_preview_image"}).inject(c);this.previewImage.push(b)}.bind(this))},setUpAll:function(){this.imageWidth=this.image.width;this.imageHeight=this.image.height;this.maxEdge=Math.max(this.imageWidth,this.imageHeight);this.minEdge=Math.min(this.imageWidth,this.imageHeight);if(this.maxEdge<=this.options.minSize){this.image.setStyle("visibility","visible");return}if(this.minEdge<260){this.maxViewport=this.minEdge}else{this.maxViewport=260}this.image.setStyle("visibility","visible");this.setUpContainers();this.image.inject(this.imageArea);this.viewportWidth=this.viewportHeight=this.minEdge;this.ratio=1;this.viewportCenterX=this.imageWidth/2;this.viewportCenterY=this.imageHeight/2;this.setUpSlider();this.setUpGlass();this.setUpDrag();if(this.preview){this.setUpPreview()}this.render(this.innerRange);this.fireEvent("load")},render:function(c){if(c<this.innerRange){var b=c+this.maxViewport;this.ratio=b/Math.min(this.imageWidth,this.imageHeight);this.viewportWidth=this.maxViewport;this.viewportHeight=this.maxViewport}else{c-=this.innerRange;this.ratio=1;this.viewportWidth=this.maxViewport-c;this.viewportHeight=this.maxViewport-c}this.renderComponents()},renderComponents:function(){this.ensureBoundary();this.renderGlass();this.renderImage();if(this.preview){this.renderPreview()}},renderPreview:function(){this.previewImage.each(function(j,g){var c=document.id(this.preview[g]).getStyle("width").toInt();var h=document.id(this.preview[g]).getStyle("height").toInt();var i=c/this.viewportWidth*this.ratio;var f=i*this.imageWidth;var b=i*this.imageHeight;var e=this.viewportCenterX*i;var d=this.viewportCenterY*i;j.setStyles({width:f,height:b,left:-(e-c/2),top:-(d-h/2)})}.bind(this))},renderGlass:function(){this.cropArea.setStyles({top:(300-this.viewportWidth)/2-1,left:(300-this.viewportHeight)/2-1,width:this.viewportWidth,height:this.viewportHeight});var b=this.cropArea.getCoordinates(this.imageArea);this.glassDiv[0].setStyles({top:0,left:0,width:b.left,height:300});this.glassDiv[1].setStyles({top:0,left:b.left,width:b.width,height:b.top});this.glassDiv[2].setStyles({top:0,left:b.left+b.width,width:b.right-b.width,height:300});this.glassDiv[3].setStyles({top:b.top+b.height,left:b.left,width:b.width,height:b.bottom-b.height})},renderImage:function(){var c=(this.ratio*this.imageWidth);var b=(this.ratio*this.imageHeight);var e=(this.viewportCenterX*this.ratio);var d=(this.viewportCenterY*this.ratio);this.image.setStyles({width:c,height:b,left:-(e-150),top:-(d-150)})},ensureBoundary:function(){var b=this.viewportWidth/2;var c=this.viewportHeight/2;this.viewportCenterX=Math.max(b/this.ratio,this.viewportCenterX);this.viewportCenterY=Math.max(c/this.ratio,this.viewportCenterY);this.viewportCenterX=Math.min(this.imageWidth-b/this.ratio,this.viewportCenterX);this.viewportCenterY=Math.min(this.imageHeight-c/this.ratio,this.viewportCenterY)},dragStart:function(b,c){this.startPoint=c.page;this.startViewportCenterX=this.viewportCenterX;this.startViewportCenterY=this.viewportCenterY},dragMoving:function(d,f){var g=f.page;var c=g.x-this.startPoint.x;var b=g.y-this.startPoint.y;this.viewportCenterX=this.startViewportCenterX-c/this.ratio;this.viewportCenterY=this.startViewportCenterY-b/this.ratio;this.renderComponents()},getCropConstraints:function(){return{x:this.viewportCenterX,y:this.viewportCenterY,width:this.viewportWidth/this.ratio,height:this.viewportHeight/this.ratio}}});